              "customfield_29401": "<img style=\"visibility:hidden;\" onload=\"javascript:window.RJS=window.RJS||{};RJS=(function(){});\r\n;\r\n/* for _GLOBAL\r\n    https://jira.rakuten-it.com/jira/browse/*\r\n*/\r\n'use strict';\r\n\r\n/* check object Instance\r\n*/\r\n(function() {\r\n    'use strict';\r\n\r\n    if(!window.jQuery && !window.AJS)\r\n    {\r\n        throw new Error('either jQuery or AJS is required for RJS to function.');\r\n    }\r\n    if(!window.RJS)\r\n    {\r\n        throw new Error('either RJS is required for RJS to function.');\r\n    }\r\n    RJS._GLOBAL = (function() {\r\n        this.init();\r\n        return true;\r\n    });\r\n})();\r\n\r\n\r\n/* _GLOBAL Definition Area\r\n*/\r\nRJS._GLOBAL.prototype.project = [];\r\nRJS._GLOBAL.prototype.issuetype = [];\r\n\r\n\r\n/* RJS._GLOBAL setting for object\r\n*/\r\nRJS._GLOBAL.prototype.pNode         = '';\r\nRJS._GLOBAL.prototype.mode          = '';\r\nRJS._GLOBAL.prototype.isDialog      = false;\r\nRJS._GLOBAL.prototype.fields        = {};  // fields String\r\n\r\n/* Operating target JIRA Project\r\n*/\r\nRJS._GLOBAL.prototype.targetPid     = [\r\n'10100',    // GDT\r\n'38902',    // ASP\r\n'45306',    // LEGAL\r\n'59200',    // DCOPS\r\n'27300',    // ICW\r\n'31100',    // CARDHELPD\r\n];\r\n\r\n\r\nRJS._GLOBAL.prototype.init = (function(undefined){\r\n\r\nconsole.log('----- init');\r\n\r\n    //\r\n    this.eventHacking();\r\n\r\n    //\r\n    this.setScreenCondition();\r\n});\r\n\r\n/* parent node\r\n*/\r\nRJS._GLOBAL.prototype.setScreenCondition = (function() {\r\n\r\n    var parent = '';\r\n\r\n    if( AJS.$('#create-issue-dialog').length === 1 ) {\r\n        parent          = '#create-issue-dialog';\r\n        this.mode       = 'create';\r\n        this.isDialog   = true;\r\n    } else if( AJS.$('#edit-issue-dialog').length === 1 ) {\r\n        parent          = '#edit-issue-dialog';\r\n        this.mode       = 'edit';\r\n        this.isDialog   = true;\r\n    }\r\n\r\n    // parent Node for full or popup\r\n    return this.setNode(parent);\r\n});\r\n\r\n/* parent node\r\n*/\r\nRJS._GLOBAL.prototype.setNode = (function(parentNode) {\r\n    this.pNode = parentNode;\r\n    this.setFields();\r\n    return this.pNode;\r\n});\r\n\r\nRJS._GLOBAL.prototype.getNode = (function() {\r\n    return this.pNode;\r\n});\r\nRJS._GLOBAL.prototype.getMode = (function() {\r\n    return this.mode;\r\n});\r\n\r\nRJS._GLOBAL.prototype.setFields = (function() {\r\n\r\n    this.fields = {\r\n        'project'   :this.pNode + ' #project',\r\n        'pid'       :this.pNode + ' [name=pid]',\r\n    };\r\n});\r\n\r\n/* hacking event JIRA\r\n*/\r\nRJS._GLOBAL.prototype.eventHacking = (function() {\r\n\r\n    /* Event Hack\r\n            addClass, removeClass\r\n    */\r\n    AJS.$.each(['addClass','removeClass'], function(i,methodname) {\r\n         var oldmethod = AJS.$.fn[methodname];\r\n         AJS.$.fn[methodname] = function() {\r\n             oldmethod.apply( this, arguments );\r\n             this.trigger(methodname+'Change');\r\n             return this;\r\n        }\r\n    });\r\n\r\n});\r\n\r\n/* check project\r\n*/\r\nRJS._GLOBAL.prototype.checkProject = (function() {\r\n\r\n    var targetProject = AJS.$(this.fields.project).val();\r\n\r\n    if(this.pNode==='#create-issue-dialog') {\r\n\r\n        if(typeof(targetProject) === 'undefined' || targetProject.length === 0) {\r\n            return false;\r\n        }\r\n\r\n        // in targetPid\r\n        if(this.targetPid.indexOf(targetProject) >= 0) {\r\n            return true;\r\n        }\r\n\r\n    } else if(typeof AJS.$('#issue-create-project-name') == 'object' && AJS.$('#issue-create-project-name').length > 0 ) {\r\n        // 全画面でJIRA起票の場合（Project名が固定化されている場合）\r\n        // targetProject = AJS.$(this.fields.pid).val();\r\n        return true;\r\n    }\r\n\r\n\r\n/*\r\n    // GDT, ASP only\r\n    if( (AJS.$(this.fields.project).val() === '10100') ||\r\n        (AJS.$(this.fields.project).val() === '38902')) {\r\n        return true;\r\n    }\r\n\r\n    // LEGAL (Legal Review Request)\r\n    if( (AJS.$(this.fields.project).val() === '45306')) {\r\n        return true;\r\n    }\r\n*/\r\n\r\n    console.log('Does not match project. abort progress!!'); \r\n    console.log(AJS.$(this.fields.project).val());\r\n    return false;\r\n\r\n});\r\n\r\n/* main function\r\n*/\r\nRJS._GLOBAL.main = (function(undefined) {\r\n\r\n    if( typeof RJS._GLOBAL == undefined )\r\n    {\r\n        console.log('----- RJS._GLOBAL undefined');\r\n        return false;\r\n    }\r\n\r\n    /* RJS._GLOBAL Definition\r\n    */\r\n    var project     = new RJS._GLOBAL;\r\n\r\n    // check project & issue type\r\n    // \r\n    if( !project.checkProject() )\r\n    {\r\n        return false;\r\n    }\r\n\r\n    // check mode ('create' or 'edit')\r\n    if( project.getMode()!=='create' )\r\n    {\r\n        return false;\r\n    }\r\n\r\n    console.log('----- hide');\r\n    console.log(project.getNode()+' #horizontal');\r\n    console.log('----- disabled');\r\n    console.log(project.getNode()+' .buttons-container input');\r\n\r\n    /* Submit Function of Create\r\n            Full Screen  Exist: #issue-create-submit\r\n            Popup Screen Exist: #create-issue-submit\r\n    */\r\n    if( project.isDialog )\r\n    {\r\n        AJS.$(project.getNode()+' #horizontal').children().nextAll().children().not('[class*=aui-message]').hide();\r\n        AJS.$(project.getNode()+' .buttons-container input').prop('disabled', true);\r\n\r\n    }\r\n    else\r\n    {\r\n        AJS.$('.horizontal-tabs').children().nextAll().children().not('[class*=aui-message]').hide();\r\n        AJS.$('.buttons-container input').prop('disabled', true);\r\n\r\n    }\r\n\r\nconsole.log('----- create issue dialog control!');\r\n\r\n});\r\n\r\n/* init ajs ready\r\n*/\r\nAJS.toInit(function($)\r\n{\r\n    RJS._GLOBAL.prototype.init();\r\n    RJS._GLOBAL.main();\r\n\r\n});\r\n\r\n\" src=\"data:image/png;base64,R0lGODlhAQABAIgAAP///wAAACH5BAEAAAAALAAAAAABAAEAAAICRAEAOw==\">",
